mapper.select:
SELECT *FROM (SELECT ROWNUM AS rr, A.* FROM (SELECT noti_no ,subject ,content ,readcount ,writer ,vdate ,regdate ,state FROM noticet WHERE state = 'A'
AND noti_no = #{noti_no}
ORDER BY noti_no DESC) a)

mapper.insert:
SELECT NVL(MAX(noti_no),0)+1 FROM noticet

INSERT INTO noticet
(noti_no
,subject
,content
,writer
,vdate)
VALUES
(#{noti_no}
,#{subject}
,#{content}
,#{writer }
,#{vdate  })
